package com.basicop;

public class Class2Relational {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;
		int b = 10;
		int c = 5;
		
		System.out.println(a==b);
		System.out.println(a!=b);
		System.out.println(a==c);
		System.out.println(a<b);
		System.out.println(a<c);
		System.out.println(b>c);
		System.out.println(b>=a);
		System.out.println(a>=c);
		System.out.println(a>=b);

	}

}
